import React, { Component } from "react";
import {
  ButtonToolbar,
  DropdownButton,
  Dropdown,
  Table
} from "react-bootstrap";
import { authenticationService } from "../services";

class HomePage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      currentUser: "",
      data: {},
      gear: {}
    };
  }

  componentDidMount() {
    const currentUser = localStorage.getItem("currentUser");
    if (currentUser) {
      this.setState({ currentUser });
      authenticationService.getUserData(currentUser, this);
      authenticationService.getGearData(currentUser, this);
    }
  }

  render() {
    const { data, gear } = this.state;
    console.log(gear[0]);
    //console.log(gear[0]);
    return (
      <div className="jumbotron">
        <h2>
          {" "}
          LV.{data.level} {data.name}
        </h2>
        <Table striped bordered hover variant="dark">
          <tbody>
            <tr>
              <th>Attack</th>
              <th>{data.attack}</th>
            </tr>
            <tr>
              <th>defence</th>
              <th>{data.defence}</th>
            </tr>

            <tr>
              <th>
                <DropdownButton
                  title="weapon"
                  variant="danger"
                  id="weapon"
                  key="weapon"
                >
                  {gear[0] === "no weapon" && (
                    <Dropdown.Item eventKey="1"> {gear[0]}</Dropdown.Item>
                  )}
                  {gear[0] && gear[0] !== "no weapon" && (
                    <Dropdown.Item eventKey="1">{gear[0].name} </Dropdown.Item>
                  )}
                  {gear[0] && gear[0] !== "no weapon" && (
                    <Dropdown.Item eventKey="1">
                      Attack: {gear[0].attack}{" "}
                    </Dropdown.Item>
                  )}
                  {gear[0] && gear[0] !== "no weapon" && (
                    <Dropdown.Item eventKey="1">
                      Defence: {gear[0].defence}{" "}
                    </Dropdown.Item>
                  )}
                </DropdownButton>
              </th>

              <th>
                <DropdownButton
                  title="armor"
                  variant="danger"
                  id="armor"
                  key="armor"
                >
                  {gear[1] === "no armor" && (
                    <Dropdown.Item eventKey="1"> {gear[1]}</Dropdown.Item>
                  )}
                  {gear[1] && gear[1] !== "no armor" && (
                    <Dropdown.Item eventKey="1">{gear[1].name} </Dropdown.Item>
                  )}
                  {gear[1] && gear[1] !== "no armor" && (
                    <Dropdown.Item eventKey="1">
                      Attack: {gear[1].attack}{" "}
                    </Dropdown.Item>
                  )}
                  {gear[1] && gear[1] !== "no armor" && (
                    <Dropdown.Item eventKey="1">
                      Defence: {gear[1].defence}{" "}
                    </Dropdown.Item>
                  )}
                </DropdownButton>
              </th>

              <th>
                <DropdownButton
                  title="skill"
                  variant="danger"
                  id="skill"
                  key="skill"
                >
                  {gear[2] === "no skill" && (
                    <Dropdown.Item eventKey="2"> {gear[2]}</Dropdown.Item>
                  )}
                  {gear[2] && gear[2] !== "no skill" && (
                    <Dropdown.Item eventKey="1">{gear[1].name} </Dropdown.Item>
                  )}
                  {gear[2] && gear[2] !== "no skill" && (
                    <Dropdown.Item eventKey="1">
                      Attack: {gear[2].attack}{" "}
                    </Dropdown.Item>
                  )}
                  {gear[2] && gear[2] !== "no skill" && (
                    <Dropdown.Item eventKey="1">
                      Defence: {gear[2].defence}{" "}
                    </Dropdown.Item>
                  )}
                </DropdownButton>
              </th>
            </tr>
          </tbody>
        </Table>
      </div>
    );
  }
}

export { HomePage };
