import React, { Component } from "react";
import { BrowserRouter as Router, Route } from "react-router-dom";
import { MyRoute } from "./components";
import { Login, HomePage } from "./containers";

class App extends Component {
  render() {
    return (
      <div className="jumbotron">
        <div className="container">
          <div className="col-sm-8 col-sm-offset-2">
            <Router>
              <div>
                <MyRoute exact path="/" component={HomePage} />
                <Route path="/login" component={Login} />
              </div>
            </Router>
          </div>
        </div>
        <div class="text-center">
          <p>
            <a href="http://jasonwatmore.com/post/2018/09/11/react-basic-http-authentication-tutorial-example">
              Powered by Loopback 4
            </a>
          </p>
        </div>
      </div>
    );
  }
}

export { App };
