import React, { Component } from "react";
import { Button, FormGroup, FormControl, FormLabel } from "react-bootstrap";
const axios = require("axios");

class Signup extends Component {
  constructor(props) {
    super(props);

    this.state = {
      email: "",
      password: "",
      name: "",
      error: null,
      submitted: false,
      loading: false
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleNameError() {
    const { submitted, name } = this.state;
    if (submitted && !name) {
      return <div className="help-block">Character name is required</div>;
    }
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  }

  handleSubmit = e => {
    e.preventDefault();
    const { email, password, name } = this.state;

    this.setState({ submitted: true });
    if (!(email && password && name)) {
      return;
    }

    this.setState({ loading: true });
    const header = {
      "Content-Type": "application/json"
    };
    const data = {
      email: email,
      password: password,
      name: name
    };
    axios
      .post("http://[::1]:3000/characters", data, header)
      .then(function(response) {
        console.log(response);
      })
      .catch(
        function(error) {
          this.setState({ error });
        }.bind(this)
      )
      .then(
        function() {
          this.setState({ loading: false });
        }.bind(this)
      );
  };

  render() {
    const { email, password, name, error, loading, submitted } = this.state;
    return (
      <React.Fragment>
        <h2>Signup</h2>
        {error && error.response.data.error.statusCode === 400 && (
          <div className={"alert alert-danger"}>This email already exists </div>
        )}
        {error && error.response.data.error.statusCode !== 400 && (
          <div className={"alert alert-danger"}>Unknow error</div>
        )}
        <form name="form" onSubmit={this.handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Character Name</label>
            <input
              type="name"
              className="form-control"
              name="name"
              value={name}
              onChange={this.handleChange}
            />
            {submitted && !name && (
              <div className="help-block">Username is required</div>
            )}
          </div>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              className="form-control"
              name="email"
              value={email}
              onChange={this.handleChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              className="form-control"
              name="password"
              value={password}
              onChange={this.handleChange}
            />
          </div>
          <div className="form-group">
            <button className="btn btn-primary" disabled={loading}>
              Signup
            </button>
            {loading && (
              <img src="data:image/gif;base64," />
            )}
          </div>
        </form>
      </React.Fragment>
    );
  }
}

export { Signup };
