import React, { Component } from "react";
import {
  ButtonToolbar,
  DropdownButton,
  Dropdown,
  Table
} from "react-bootstrap";
import { authenticationService } from "../services";

class HomePage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      currentUser: "",
      data: {},
      gear: {}
    };
  }

  componentDidMount() {
    const currentUser = localStorage.getItem("currentUser");
    if (currentUser) {
      this.setState({ currentUser });
      authenticationService.getUserData(currentUser, this);
      authenticationService.getGearData(currentUser, this);
    }
  }

  render() {
    const { data, gear } = this.state;
    console.log(gear[0]);
    console.log(gear[0]);
    return (
      <div className="jumbotron">
        <h2>
          {" "}
          LV.{data.level} {data.name}
        </h2>
        <Table striped bordered hover variant="dark">
          <tbody>
            <tr>
              <th>Attack</th>
              <th>{data.attack}</th>
            </tr>
            <tr>
              <th>defence</th>
              <th>{data.defence}</th>
            </tr>

            <tr>
              <DropdownButton
                title="weapon"
                variant="danger"
                id="weapon"
                key="weapon"
              >
                {gear[0] === "no weapon" && (
                  <Dropdown.Item eventKey="1"> {gear[0]}</Dropdown.Item>
                )}
                {gear[0] !== "no weapon" && (
                  <Dropdown.Item eventKey="1"> </Dropdown.Item>
                )}
              </DropdownButton>
            </tr>
          </tbody>
        </Table>
      </div>
    );
  }
}

export { HomePage };
