import React, { Component } from "react";
import { BrowserRouter as Router, Route } from "react-router-dom";
import { MyRoute, NavBar } from "./components";
import { Login, HomePage } from "./containers";
import { authenticationService } from "./services";

const axios = require("axios");

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentUser: "",
      data: {}
    };
    this.handelLogout = this.handelLogout.bind(this);
  }
  componentDidMount() {
    const currentUser = localStorage.getItem("currentUser");
    if (currentUser) {
      this.setState({ currentUser });
      axios
        .get("http://[::1]:3000/characters", {
          headers: { Authorization: `Bearer ${currentUser}` }
        })
        .then(
          function(response) {
            console.log(response);
            //this.setState();
          }.bind(this)
        )
        .catch(
          function(error) {
            console.log(error.response);
          }.bind(this)
        );
    }
  }

  handelLogout() {
    authenticationService.logout();
    this.setState({ currentUser: "", data: {} });
  }

  render() {
    const { currentUser, data } = this.state;
    return (
      <div className="jumbotron">
        {currentUser && <NavBar data={data} onLogout={this.handelLogout} />}
        <div className="container">
          <div className="col-sm-8 col-sm-offset-2">
            <Router>
              <div>
                <MyRoute exact path="/" component={HomePage} />
                <Route path="/login" component={Login} />
              </div>
            </Router>
          </div>
        </div>
        <div className="text-center">
          <p>
            <a href="https://loopback.io/">Powered by Loopback 4</a>
          </p>
          <p>
            <a href="https://github.com/gobackhuoxing/first-web-game-lb4">
              Github@gobackhuoxing
            </a>
          </p>
        </div>
      </div>
    );
  }
}

export { App };
