export const authenticationService = {
  login,
  signup,
  logout,
  getUserData,
  getGearData
};

const axios = require("axios");

function login(email, password, self, unmount) {
  const data = {
    email: email,
    password: password
  };
  const header = {
    "Content-Type": "application/json"
  };
  return axios
    .post("http://[::1]:3000/characters/login", data, header)
    .then(function(response) {
      //console.log(response.data.token);
      localStorage.setItem("currentUser", response.data.token);
      self.props.history.push("/");
    })
    .catch(function(error) {
      //console.log("error");
      if (!unmount) self.setState({ error });
    });
}

function signup(email, password, name) {
  const header = {
    "Content-Type": "application/json"
  };
  const data = {
    email: email,
    password: password,
    name: name
  };
  return axios
    .post("http://[::1]:3000/characters", data, header)
    .then(function(response) {
      console.log(response);
    });
}

function getUserData(currentUser, self) {
  axios
    .get("http://[::1]:3000/characters", {
      headers: { Authorization: `Bearer ${currentUser}` }
    })
    .then(function(response) {
      //console.log(response.data);
      self.setState({ data: response.data });
    })
    .catch(function(error) {
      logout();
    });
}

function getGearData(currentUser, self) {
  axios
    .get("http://[::1]:3000/updatecharacter", {
      headers: { Authorization: `Bearer ${currentUser}` }
    })
    .then(function(response) {
      //console.log(response.data);
      self.setState({ gear: response.data });
    })
    .catch(function(error) {
      logout();
    });
}

function logout() {
  localStorage.removeItem("currentUser");
}
