import React, { Component } from "react";
import "./style.css";

class InitCharacter extends Component {
  unmount = false;
  constructor(props) {
    super(props);

    this.state = {
      email: "",
      password: "",
      confirmPassword: "",
      error: null,
      submitted: false,
      loading: false,
      shortPassword: false
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handelClick = e => {
    console.log(e.target);
    e.target.classList.toggle("open");
  };
  handleSubmit() {}
  render() {
    return (
      <React.Fragment>
        <div className="panels">
          <div className="panel panel1" onClick={this.handelClick}>
            <p>Hey</p>
            <p>Let's</p>
            <p>Dance</p>
          </div>
          <div className="panel panel2" onClick={this.handelClick}>
            <p>Give</p>
            <p>Take</p>
            <p>Receive</p>
          </div>
          <div className="panel panel3" onClick={this.handelClick}>
            <p>Experience</p>
            <p>It</p>
            <p>Today</p>
          </div>
        </div>
        <form name="form" onSubmit={this.handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Character Name</label>
            <input
              type="name"
              className="form-control"
              name="name"
              value={name}
              onChange={this.handleChange}
            />
            {submitted && !email && (
              <div className="alert alert-danger">email is required</div>
            )}
          </div>
        </form>
      </React.Fragment>
    );
  }
}

export { InitCharacter };
