import React, { Component } from "react";
import { BehaviorSubject } from "rxjs";

const axios = require("axios");
const currentUserSubject = new BehaviorSubject(
  JSON.parse(localStorage.getItem("currentUser"))
);

class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      email: "",
      password: "",
      error: null,
      submitted: false,
      loading: false
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleNameError() {
    const { submitted, name } = this.state;
    if (submitted && !name) {
      return <div className="help-block">Character name is required</div>;
    }
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  }

  handleSubmit = e => {
    e.preventDefault();

    this.setState({ submitted: true });
    const { email, password } = this.state;
    if (!(email && password)) {
      return;
    }

    this.setState({ loading: true });
    const header = {
      "Content-Type": "application/json"
    };
    const data = {
      email: email,
      password: password
    };
    axios
      .post("http://[::1]:3000/characters/login", data, header)
      .then(function(response) {
        console.log(response);
      })
      .catch(
        function(error) {
          this.setState({ error });
        }.bind(this)
      )
      .then(
        function() {
          this.setState({ loading: false });
        }.bind(this)
      )
      .then(user => {
        // store user details and jwt token in local storage to keep user logged in between page refreshes
        localStorage.setItem("currentUser", JSON.stringify(user));
        currentUserSubject.next(user);
        console.log(user);
        return user;
      });
  };

  render() {
    const { email, password, error, loading, submitted } = this.state;
    return (
      <React.Fragment>
        <h2>Login</h2>
        {error && error.response.data.error.statusCode === 404 && (
          <div className={"alert alert-danger"}>Email is incorrect </div>
        )}
        {error && error.response.data.error.statusCode === 401 && (
          <div className={"alert alert-danger"}>Password is incorrect </div>
        )}
        {error && error.response.data.error.statusCode === 422 && (
          <div className={"alert alert-danger"}>
            Email or Password is invalid
          </div>
        )}
        <form name="form" onSubmit={this.handleSubmit}>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              className="form-control"
              name="email"
              value={email}
              onChange={this.handleChange}
            />
          </div>
          {submitted && !email && (
            <div className="alert alert-danger">Email is required</div>
          )}
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              className="form-control"
              name="password"
              value={password}
              onChange={this.handleChange}
            />
          </div>
          {submitted && !password && (
            <div className="alert alert-danger">Password is required</div>
          )}
          <div className="form-group">
            <button className="btn btn-primary" disabled={loading}>
              Signup
            </button>
            {loading && (
              <img src="data:image/gif;base64," />
            )}
          </div>
        </form>
      </React.Fragment>
    );
  }
}
export { Login };
