import React, { Component } from "react";
import { authenticationService } from "../services";
import { Navbar, Button } from "react-bootstrap";

class Signup extends Component {
  constructor(props) {
    super(props);

    this.state = {
      email: "",
      password: "",
      confirmPassword: "",
      name: "",
      error: null,
      submitted: false,
      loading: false,
      shortPassword: false,
      jumpTo: "/signup"
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  }

  handleSubmit = e => {
    e.preventDefault();
    const { email, password, name, confirmPassword } = this.state;

    this.setState({ submitted: true });
    if (!(email && password && name && confirmPassword)) {
      return;
    }
    if (password.length < 8) {
      this.setState({ shortPassword: true });
      return;
    }
    if (password !== confirmPassword) {
      return;
    }

    this.setState({ loading: true });

    authenticationService
      .signup(email, password, name)
      .catch(
        function(error) {
          this.setState({ error });
        }.bind(this)
      )
      .then(
        function() {
          this.setState({ loading: false });
          if (!error) {
            this.setState({ jumpTo: "/login" });
          }
        }.bind(this)
      );
  };

  render() {
    const {
      email,
      password,
      confirmPassword,
      name,
      error,
      loading,
      submitted,
      shortPassword,
      jumpTo
    } = this.state;
    return (
      <React.Fragment>
        <h2>Signup</h2>
        {error && error.response.data.error.statusCode === 400 && (
          <div className={"alert alert-danger"}>This email already exists </div>
        )}
        {error && error.response.data.error.statusCode !== 400 && (
          <div className={"alert alert-danger"}>Unknow error</div>
        )}
        <form name="form" onSubmit={this.handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Character Name</label>
            <input
              type="name"
              className="form-control"
              name="name"
              value={name}
              onChange={this.handleChange}
            />
            {submitted && !name && (
              <div className="alert alert-danger">Username is required</div>
            )}
          </div>

          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              className="form-control"
              name="email"
              value={email}
              onChange={this.handleChange}
            />
            {submitted && !email && (
              <div className="alert alert-danger">email is required</div>
            )}
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              className="form-control"
              name="password"
              value={password}
              onChange={this.handleChange}
            />
            {submitted && !password && (
              <div className="alert alert-danger">Password is required</div>
            )}
            {submitted && shortPassword && (
              <div className="alert alert-danger">
                Password too short - minimum length is 8 characters
              </div>
            )}
          </div>

          <div className="form-group">
            <label htmlFor="password">Confirm Password</label>
            <input
              type="password"
              className="form-control"
              name="confirmPassword"
              value={confirmPassword}
              onChange={this.handleChange}
            />
            {submitted && !password && (
              <div className="alert alert-danger">
                Confirm Password is required
              </div>
            )}
            {submitted && password !== confirmPassword && (
              <div className="alert alert-danger">Password doesn't match</div>
            )}
          </div>

          <div className="form-group">
            <Button
              className="btn btn-primary"
              href={jumpTo}
              disabled={loading}
            >
              Signup
            </Button>
            {loading && (
              <img src="data:image/gif;base64," />
            )}
          </div>
        </form>
      </React.Fragment>
    );
  }
}

export { Signup };
