import React, { Component } from "react";
import { Display, InitCharacter } from "../components";
import "./style.css";

class HomePage extends Component {
  constructor(props) {
    super(props);

    this.state = { name: props.name };
  }

  componentDidMount() {
    const { handelUserData, data } = this.props;
    if (!data) this.props.history.push("/login");
    handelUserData();
  }

  render() {
    const { data, gear, currentUser, handelUserData } = this.props;
    const { name } = this.state;
    return (
      <React.Fragment>
        <div className="basic">
          {data.name && data.name !== "nousername" && (
            <h2>
              LV.{data.level} {name}
            </h2>
          )}
          {name !== "nousername" && (
            <Display className="basic" data={data} gear={gear} />
          )}

          {name === "nousername" && (
            <InitCharacter
              className="basic"
              currentUser={currentUser}
              data={data}
              handelUserData={handelUserData}
              homePage={this}
            />
          )}
        </div>
      </React.Fragment>
    );
  }
}

export { HomePage };
