import React, { Component } from "react";
import { BrowserRouter as Router, Route } from "react-router-dom";
import { MyRoute, NavBar } from "./components";
import { Login, HomePage } from "./containers";

const axios = require("axios");

class App extends Component {
  state = {
    currentUser: ""
  };

  componentDidMount() {
    const currentUser = localStorage.getItem("currentUser");
    if (currentUser) {
      const header = {
        Authorization: `Bearer ${currentUser}`
      };
      console.log(header);
      axios
        .get("http://[::1]:3000/characters", {
          headers: { Authorization: "Header-Value" }
        })
        .then(function(response) {
          console.log(response);
        })
        .catch(
          function(error) {
            console.log(error.response);
          }.bind(this)
        );
    }
  }

  render() {
    const { currentUser } = this.state;
    return (
      <div className="jumbotron">
        {currentUser && <NavBar />}
        <div className="container">
          <div className="col-sm-8 col-sm-offset-2">
            <Router>
              <div>
                <MyRoute exact path="/" component={HomePage} />
                <Route path="/login" component={Login} />
              </div>
            </Router>
          </div>
        </div>
        <div className="text-center">
          <p>
            <a href="https://loopback.io/">Powered by Loopback 4</a>
          </p>
          <p>
            <a href="https://github.com/gobackhuoxing/first-web-game-lb4">
              Github@gobackhuoxing
            </a>
          </p>
        </div>
      </div>
    );
  }
}

export { App };
