export const authenticationService = {
  login,
  signup,
  logout
};

const axios = require("axios");

function login(email, password) {
  const data = {
    email: email,
    password: password
  };
  const header = {
    "Content-Type": "application/json"
  };
  return axios
    .post("http://[::1]:3000/characters/login", data, header)
    .then(function(response) {
      console.log(response.data.token);
      localStorage.setItem("currentUser", response.data.token);
    });
}

function signup(email, password, name) {
  const header = {
    "Content-Type": "application/json"
  };
  const data = {
    email: email,
    password: password,
    name: name
  };
  return axios
    .post("http://[::1]:3000/characters", data, header)
    .then(function(response) {
      console.log(response);
    })
    .catch(
      function(error) {
        this.setState({ error });
        console.log("error");
      }.bind(this)
    );
}

function logout() {
  localStorage.removeItem("currentUser");
}
