import React, { Component } from "react";
import { userService } from "../services";
import "./style.css";

class InitCharacter extends Component {
  unmount = false;
  constructor(props) {
    super(props);

    this.state = {
      name: "",
      error: null,
      submitted: false,
      loading: false
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentWillUnmount() {
    this.unmount = true;
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  }

  handleSubmit = e => {
    e.preventDefault();
    const { name } = this.state;

    this.setState({ submitted: true });
    if (!name) {
      return;
    }

    this.setState({ loading: true });

    const gear = {};

    userService.initCharacter(name, gear, this).then(
      function() {
        if (!this.unmount) this.setState({ loading: false });
      }.bind(this)
    );
  };

  handelClick = e => {
    console.log(e.target);
    e.target.classList.toggle("open");
  };
  handleSubmit() {}
  render() {
    const { name, submitted } = this.state;
    return (
      <React.Fragment>
        <div className="panels">
          <div className="panel panel1" onClick={this.handelClick}>
            <p>Hey</p>
            <p>Let's</p>
            <p>Dance</p>
          </div>
          <div className="panel panel2" onClick={this.handelClick}>
            <p>Give</p>
            <p>Take</p>
            <p>Receive</p>
          </div>
          <div className="panel panel3" onClick={this.handelClick}>
            <p>Experience</p>
            <p>It</p>
            <p>Today</p>
          </div>
        </div>
        <form name="form" onSubmit={this.handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Character Name</label>
